app.controller("mainCtrl",function(e,s,n,o){e.$on("$routeChangeSuccess",function(){e.curCategory=n.cat,e.curVendor=n.ven,e.breadcrumbs.options={Category:e.curCategory,Vendor:e.curVendor}}),e.breadcrumbs=o,s.get("js/data/reviews.json").success(function(s){e.reviews=s}),s.get("js/data/categories.json").success(function(s){e.categories=s}),s.get("js/data/vendors.json").success(function(s){e.vendors=s})});