app.controller("ratingCtrl",function(e,r,n){r.get("js/data/vendors.json").success(function(r){e.vendors=r;for(var s=0,t=0,a=0,o=0;o<r.length;o++){for(var v=0;v<r[o].reviews.length;v++)r[o].name===n.ven&&(s+=r[o].reviews[v].stars);a=r[o].reviews.length}t=Math.floor(s/a),e.reviewsNum=a,e.averageRate=t})});